<script>
import HalfBanners from '@/components/banners/HalfBanners.vue'
import BannerDark from '@/components/banners/BannerDark.vue'
import BannerWatch from '@/components/banners/BannerWatch.vue'
import BannerWhite from '@/components/banners/BannerWhite.vue'
import carousel from 'vue-owl-carousel'
export default {
  name: 'Product',
  components: {
    carousel,
    HalfBanners,
    BannerDark,
    BannerWatch,
    BannerWhite
  },
  data() {
    return {
      product: [],
      updateVisible: false,
      contentVisible: true
    }
  },
  created() {
    // let productSlug = this.$route.params.slug
    // this.product = this.$store.getters.getProductBySlug(productSlug)
  },
  computed: {
    productItem() {
      return this.$store.getters.getProductBySlug(this.$route.params.slug)
    }
  },
  beforeRouteUpdate (to, from, next) {
  //  console.log('START')
   this.updateVisible = true
   this.contentVisible = false
   next()
  },
  updated(){
    // console.log('END')
    setTimeout( () => {
      this.updateVisible = false
      this.contentVisible = true
    }, 1000)
  },
  methods: {
    updateEnd: function () {
      this.updateVisible = false
    }
  }
}
</script>

